import React, { useState } from 'react'
import { PieChart } from 'react-minimal-pie-chart';


function Pie() {
  const[input1,setInput1]=useState()
  const[input2,setInput2]=useState()

  const[data,setData]=useState([
  { labels: ['Red', 'Blue'],
    datasets: [
    {
      label: '# of Votes',
      data: [12, 19],
      backgroundColor: [
        'rgba(255, 99, 132, 0.2)',
        'rgba(255, 206, 86, 0.2)']
      }]   
      , borderWidth: 1,
    },
  ])

  const handle1Change=(e)=>{
  
    const value=e.target.value;
    if(value>100){
      alert('value is Not correct')

    }else if(value<=100)
     setInput1(value);
     setInput2(100-value);
    

    }
  

  const handle2Change=(e)=>{
    const value=e.target.value;
    
    if (value>100) {
      alert('valueis not correct')
      
    } else if(value<=100)
    {
      setInput2(value)
      setInput1(100-value);
    }
    
  }

  const handleSubmit=(e)=>{
    e.preventDefault()
    setData((prev)=>({...prev ,series:[input1,input2]}))

  }

  return (
    <div>
<div>
    <form onSubmit={handleSubmit}>
      <input type='number' name='input1' value={input1} onChange={handle1Change}/>
      <input type='number' name='input2' value={input2} onChange={handle2Change}/>
      <button>CREAT-CHART</button>
    </form>
</div>

<div>
<PieChart
series=
{[
  { data: [{ id: 0, value: '10', color: '#E38627', label:'A' },
        { id: 1, value:'20', color: '#C13C37', label:'B' }],
}
 

]}
style={{width:'400px'}}
/>;


</div>
</div>
    
  )

}
export default Pie;
