import React, { useState } from 'react'

function Conditional() {

// let [toggle,setToggle]=useState(true)

// let styles={
//   backgroundColor:'red',
//   width:'50vh',
//   height:'30vh',
//   display:'hiden',
//   border:'1px solid black',
//   display:'flex'
// }


// function change(e){
//     e.preventDefault()
//     setToggle(!toggle)
// }

  const [color,setColor]=useState({backgroundColor:'red'})

  function colors(e){
      setColor({backgroundColor:'white'})
  }


  return (
    <>             
            {/* {toggle?<div style={styles}></div> : null}
            <button onClick={(e)=>change(e)}>{toggle?'Hide':'Show'}</button>   */}
            
          <div style={{display:'flex'}} >
                 <div style={{width:'50vh',height:'30vh',border:'1px solid black',backgroundColor:color}}></div>
                 <div onClick={()=>colors()}style={{width:'50vh',height:'30vh',border:'1px solid black'}}></div>
          </div>
    
    </>
  )
}

export default Conditional
