import { useFormik } from 'formik'
import React from 'react'

function Form() {

    const formik=useFormik({
     initialValues:{
      username:'',
      email:'',
      password:'',
     },
     onSubmit:(values)=>{
      console.log(values)      
     },
     validate:(values)=>{
      let errors={};

                if(!values.username){
                  errors.username='reqired'        
                }else if(!values.username.length >8){
                  errors.username='length is not greter then 8'
                }
                if(!values.email){
                  errors.email='invalid email'
                }else if(/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(!values.email)){
                  errors.email='requird'
                }
                if(!values.password){
                  errors.password='required'
                }
      return errors
     }
    });
 
  return (
    <div  className='container border border-1 mx-auto w-30 p-3' >
        <form onSubmit={formik.handleSubmit}>
         <div className='mb-3 w-50 mx-auto'>
            <label>UserName:-<input type="text" name='username' onChange={formik.handleChange} value={formik.values.username}/></label>
            {formik.touched.username && formik.errors.username ? <h5>{formik.errors.username}</h5> :null}
          </div>
          <div className='mb-3 w-50 mx-auto'>
             <label>Email:-<input type="email" name='email' onChange={formik.handleChange} value={formik.values.email}/></label>
             {formik.touched.email && formik.errors.email ? <h6>{formik.errors.email}</h6> :null}
          </div>
          <div className='mb-3 w-50 mx-auto'><label>Password:-<input type="password" name='password' onChange={formik.handleChange} value={formik.values.password}/></label>
          {formik.touched.password && formik.errors.password ? <h6>{formik.errors.password}</h6> : null}
          
          </div>
          <br></br>
          <button type='submit'>submit</button>
        </form>
    </div>
  )
}

export default Form
