import { Form } from 'formik'
import React from 'react'
import { useState } from 'react'

function FormRadio() {
    const [input,setInput]=useState({
        firstname:'',
        lastname:'',
        phone:'',
        email:''
    })
    const[data,setData]=useState([])

    const [contact,setContact]=useState([])

    const handlechange =(e)=>{
        console.log(e.target.value)
        setInput((prev)=>({...prev,[e.target.name]:e.target.value}))
        console.log(input)
    }

    const handleContact=(e)=>{
            let x=e.target.value
            setContact(x)
    }
    const handleSubmit=(e)=>{
        e.preventDefault()
        setData([...data,input])
        console.log(input)
        
      }
    


  return (
    <div>
        <form onSubmit={handleSubmit}>
            
            <input value={input.firstname} onChange={handlechange} placeholder='First Name'/>
        <br></br>
            <input onChange={handlechange} value={input.lastname} placeholder='Last Name'/>
                <br></br><br></br>
                <h6>How should we contact you?</h6>
            <label>
               <input type='radio' onClick={handleContact} value='phone'/>
                Phone                
            </label>    
            <label>
               <input type='radio'value='email' onClick={handleContact}/>
                Email                
            </label>
            <br></br>

            {contact === 'phone' ?<input value={input.phone} type='number' onChange={handlechange} name='phone' />:null}
            
            {contact === 'email' ? <input value={input.email} type='email' onChange={handlechange} name='email' />:null}
            <br></br>
            <button type='button'value='submit'>Submit</button>
        </form>

    </div>
  )
}

export default FormRadio
