import React from 'react'
import { useState } from 'react'
import { Accordion , AccordionSummary,Typography,AccordionDetails,Box} from '@mui/material';
import { BiChevronDown } from "react-icons/bi";
import classes from './style.module.css'


function SectionPro() {
    const [input,setInput]=useState({
        Header:'',
        Details:'',
    })
    const [data,setData]=useState([])
    

    const handlechange =(e)=>{
        const {name,value}=e.target
        setInput((prev)=>({...prev,[name]:value}))
        console.log(input)
    }
    const handlesubmit=(e)=>{
        e.preventDefault()
        setData([...data,input])
        console.log(data)
    }



  return (
    <div className={classes.Form}>
        <div>
            <form onSubmit={handlesubmit}>
                <div>
                    <h3>Add Section</h3>
                    <label>Section Header </label>
                    <input onChange={handlechange} value={input.Header} name='Header'/>

                    <label>Section Details</label>
                    <textarea onChange={handlechange} value={input.Details} name='Details'></textarea>
                </div>
                <button value='submit'>Add</button>            
            </form>
        </div>
        <div >
        {data.map((data)=>{
            return(
                <Box className={classes.Box}>
                     <Accordion className="content-section implementation">
                        <AccordionSummary
                        expandIcon={<BiChevronDown/>}
                        aria-controls="panel1a-content"
                        id="panel1a-header"
                        >
                        <Typography>{data.Header}</Typography>
                        </AccordionSummary>
                        <AccordionDetails>
                        <Typography>
                            {data.Details}
                        </Typography>
                        </AccordionDetails>
                        </Accordion>
      
                </Box> 
            )
         })}
      
        </div>



    </div>
  )
}

export default SectionPro
