import React, { useState } from 'react'
import List from './List';
import { createContext } from 'react';
export const list=createContext()
function Form() {
let [form,setForm]=useState({
  username:"",
  email:'',
  password:''
});

const [data,Setdata]=useState([])

function handleChange(e){
  const {name,value}=e.target
  setForm((prev)=>({...prev,[name]:value}))

  // setForm((prev)=>({...prev,[e.target.name]:e.target.value})) ase hi  karu shakto
  console.log(form)
};
function handleSubmit(e){
  e.preventDefault()
  if(!form.username && !form.email && !form.email){
    let x= 'plz enter the valid data'
  }else{
  Setdata([...data,form]) 
}
}
console.log(data)




  return (
    <div> 
      <h1 className='red'>form</h1>
      <form onSubmit={handleSubmit}>
        <label> UserName:-
          <input name="username" type='text'value={form.username} onChange={handleChange}/>
        </label><br/>
        <label>Email
          <input name='email' type='email' value={form.email} onChange={handleChange}/>
        </label><br/>
        <label>password:-
          <input name='password' type='password' value={form.password} onChange={handleChange}/>
        </label><br/>
        <button type='submit'>Submit</button>
      </form>
      {/* { JSON.stringify(data)} */}
     <list.Provider value={data}><List/></list.Provider>
    </div>
  )
}
export default Form
