import { useState } from 'react'

function Form() {
  const [data,setData]=useState('')

  const handleChange=(e)=>{

    console.log(e.target.value);
    let y=e.target.value;
    let x='';   
    if(y=='phone'){
      return   x=<input value='phone'/>
    }else{
     return  null
    }  
  }
  return (
    <div>

      <form>       
        <select onChange={(e)=>handleChange(e)} placeholder='select the option'>
          <option value="mail" name='mail'>mail</option>
          <option value='phone' name='phone'>Number</option>
        </select>
        {x}
      </form>




    </div>
  )
}

export default Form
