import React, { useState } from 'react'
import { PieChart } from 'react-minimal-pie-chart';


function Pie() {
  const[input1,setInput1]=useState()
  const[input2,setInput2]=useState()

  const[data,setData]=useState([
    {value:[],label:['A','B']}
  ])

  const handle1Change=(e)=>{
  
    const value=e.target.value;
    if(value>100){
      alert('value is Not correct')

    }else if(value<=100)
     setInput1(value);
     setInput2(100-value);
    

    }
  

  const handle2Change=(e)=>{
    const value=e.target.value;
    
    if (value>100) {
      alert('valueis not correct')
      
    } else if(value<=100)
    {
      setInput2(value)
      setInput1(100-value);
    }
    
  }

  const handleSubmit=(e)=>{
    e.preventDefault()
    setData((prev)=>({...prev ,series:[input1,input2]}))
    console.log(data)

  }

  return (
    <div>
<div>
    <form onSubmit={handleSubmit}>
      <input type='number' name='input1' value={input1} onChange={handle1Change}/>
      <input type='number' name='input2' value={input2} onChange={handle2Change}/>
      <button>CREAT-CHART</button>
    </form>
</div>

<div>
<PieChart style={{width:'400px'}}/>;


</div>
</div>
    
  )

}
export default Pie;
