import React, { useState } from 'react'

function Conditional() {

let [toggle,setToggle]=useState(true)

let styles={
    backgroundColor:'red',
    width:'50vh',
    height:'30vh'
}

function change(){
    e.preventDefault()
    setToggle(!toggle)
}


  return (
    <>             
            {toggle?<div style={styles}></div> : null}
            <button onClick={(e)=>change(e)}>{toggle?'hide':'show'}</button>  
            
    
    
    </>
  )
}

export default Conditional
