import axios from 'axios'
import React, { useEffect,useState } from 'react'

function ApiAxios() {

let [data,setData]=useState(null)  
const[loding,setLoding]=useState(true)

console.log(data)
const api= async()=>{
try {
  let htt= await axios.get('http://jsonplaceholder.typicode.com/users');
  setData(htt.data)
  setLoding(false)
} catch (error) {
  console.log(error)
  
}  
};

useEffect(()=>{
  api()

},[])
 
if(loding){
return <h1>loding</h1>
}


 





  return (
    <div style={{color:'red'}}>
         this is the axios
         {data.map((item)=>{

          return (<ul><li>{item.name}</li></ul>  )       
          
         })}


    </div>
  )
}

export default ApiAxios
