import React, { useState } from 'react'

function Conditional() {

let [toggle,setToggle]=useState(true)

let styles={
    backgroundColor:'red',
    width:'50vh',
    height:'30vh',
    display:'hiden',
    border:'1px solid black',
    display:'flex'
}

function change(e){
    e.preventDefault()
    setToggle(!toggle)
}

const [color,setColor]=useState(true)

  return (
    <>             
            {toggle?<div style={styles}></div> : null}
            <button onClick={(e)=>change(e)}>{toggle?'Hide':'Show'}</button>  
            
          <div style={{display:'flex'}} >
                

              <div style={styles}></div>
              <div style={styles}></div>














          </div>
    
    </>
  )
}

export default Conditional
