import { useFormik } from 'formik'
import React from 'react'

function Form() {

    const formik=useFormik({
     initialValues:{
      username:'',
      email:'',
      password:'',
     },
     onSubmit:(values)=>{
      console.log(values)      
     },
     validate:(values)=>{
      let error={};

      if(!values.username){
        error.username='reqired'        
      }else if(!values.username.length >8){
        error.username='length is not greter then 8'
      }
      if(!values.email){
        error.mail='invalid email'
      }else if(/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)){
        error.email=''

      }




     }
    });
 
  return (
    <div>
        <form onSubmit={formik.handleSubmit}>
          <label>UserName:-<input type="text" name='username' onChange={formik.handleChange} value={formik.values.username}/>
          </label>
          <label>Email:-<input type="email" name='email' onChange={formik.handleChange} value={formik.values.email}/>
          </label>
          <label>Password:-<input type="password" name='password' onChange={formik.handleChange} value={formik.values.password}/>
          </label>
          <button type='submit'>submit</button>
        </form>
    </div>
  )
}

export default Form
