import React, {  useState } from 'react'

import classes from './style.module.css'

function Form() {

  const [input,setInput]=useState({
    FirstName : '',
    LastName :'',
    Email : '',
    Number : ''

  })
  const [data,setData]=useState([])
  const[contactDetail,setContactDetail]=useState({})  

  const handleChange=(e)=>{
      // console.log(e.target.value)
      // console.log(e.target.name)
      setInput((prev)=>({...prev,[e.target.name]:e.target.value}))
      console.log(input)      
  }

  const handleContact =(e)=>{
    const target=e.target;
    console.log(target.type);
    console.log(target.value);
    const value = target.value;
    const name = target.name;

    
    setContactDetail({...contactDetail, [name]: value})
    console.log(contactDetail)   
  }


  const handleSubmit=(e)=>{
    e.preventDefault()
    setData([...data,input])
    console.log(input)
    
  }
 
  return (
    <div className={classes.Form}>
      <form >
        <input type='text' placeholder='UserName' onChange={handleChange} value={input.FirstName} name='FirstName'/>
        <input type='text' placeholder='LastName'onChange={handleChange} value={input.LastName} name='LastName'/>
        <br/><br/>       
	      <select name='isemailselected' onChange={handleContact} value={contactDetail['isemailselected']}>
          <option>Open this select menu</option>
          <option value='Yes'>Mail</option>
          <option value='No'>Mobile Number</option>        
	      </select>
     
        { contactDetail['isemailselected'] === 'Yes' ?
                   <>
                       <label >Enter Email</label>
                       <input type="text" onChange={handleChange} value={input.Email} name="Email" />
                   </>
                   :null

               }
        { contactDetail['isemailselected'] === 'No' ? <><label>Enter Mobile</label>
                                                        <input type="text" onChange={handleChange} value={input.Number} name='Number'/>
                    </>
                   :null

               }
       
        <br/><br/>

        <button onClick={handleSubmit}>SUBMIT</button>
      </form>
      

    </div>
  )
}

export default Form
