import React, { useState } from 'react'

function Conditional() {

let [toggle,setToggle]=useState(false)

let styles={
    backgroundColor:'red',
    width:'50vh',
    height:'30vh'
}

function change(){
    setToggle(true)
}


  return (
    <>             
            {!toggle?<div style={styles}></div> : null}
            <button onClick={change}>{!toggle?'show':'hide'}</button>  
            
    
    
    </>
  )
}

export default Conditional
