import React, { useState } from 'react'

function Conditional() {

let [div,setDiv]=useState(true)

let styles={
    backgroundColor:'red',
    width:'50vh',
    height:'30vh'
}

function change(){

    setDiv(false)

}


  return (
    <>
            {div?<div style={styles}></div> : null}
            <button onClick={change}>{div}</button>    
    
    
    </>
  )
}

export default Conditional
