import React, { useState } from 'react'

function Conditional() {

let [toggle,setToggle]=useState(true)

let styles={
    backgroundColor:'red',
    width:'50vh',
    height:'30vh',
    display:'hiden',
    border:'1px solid black',
}

function change(e){
    e.preventDefault()
    setToggle(!toggle)
}


  return (
    <>             
            {toggle?<div style={styles}></div> : null}
            <button onClick={(e)=>change(e)}>{toggle?'Hide':'Show'}</button>  
            
          <div >
                 <div style={styles}></div>
                 <div style={styles}></div>
          </div>
    
    </>
  )
}

export default Conditional
