import React, { useState } from 'react'
import './style.css'
function Conditional() {

// let [toggle,setToggle]=useState(true)



// function change(e){
//     e.preventDefault()
//     setToggle(!toggle)
// };
const [colortoggle,setColorToogle]=useState(true);

 const red={backgroundColor:'red'}

function handle(){

  setColorToogle(!colortoggle)

}






  return (
    <>             
            {/* {toggle?<div style={styles}></div> : null}
            <button onClick={(e)=>change(e)}>{toggle?'Hide':'Show'}</button>   */}
            
          <div className='container'>
              <div style={{backgroundColor: colortoggle && red}} onClick={handle}>1</div>
              <div style={{backgroundColor: !colortoggle && red}} onClick={handle}>2</div>
          </div>
    
    </>
  )
}

export default Conditional
