import React, { useState } from 'react'

function Conditional() {

let [toggle,setToggle]=useState(true)

let styles={
    backgroundColor:'red',
    width:'50vh',
    height:'30vh'
}

function change(){

    setToggle(false)

}


  return (
    <>
              <button onClick={change}>{!toggle?'Hide':'show'}</button>  
            {div?<div style={styles}></div> : null}
            
    
    
    </>
  )
}

export default Conditional
