import React, { useState } from 'react'

function Postdata() {
  
  const [data,setData] = useState(null);
  const[setLoading] = useState(true)
  console.log(data)

 const postData= async(d)=>{
 try{
  const data= await axios.post("https://jsonplaceholder.typicode.com/users",d)
  setData(data.data)
  setLoading(false)
 }
 catch(error){
  console.log(error)
 }

  
 }

const handleClick=((e)=>{
  
  postData({id:1,data:"lorem ipsum",title:"post"})
})

return(

<div>
 
 <button type='submit' onClick={handleClick}></button>




</div>



)



}

